import React, { Component } from 'react'
import axios from 'axios'
import ListItem from '../components/ListItem'
import Footer from '../components/Footer'
export default class TodoList extends Component {
  constructor() {
    super()
    this.state = {
      listItem: '',
      todoList: [],
      deadline: ''
    }
  }
  getAllTodos = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/todos')
      console.log(response)
    } catch (error) {
      console.log('error')
    }
  }

  clearlist(e) {
    this.setState({
      todoList: []
    })
  }

  handleChange = (event) => {
    this.setState({ listItem: event.target.value })
  }
  handleSubmit = async (e) => {
    e.preventDefault()
    try {
      console.log('submitted firing off')
      //axios call
      let response = await axios.post('http://localhost:3001/api/todos/add', {
        listItem: this.state.listItem
      })
      console.log(response.data)
      const newArray = this.state.todoList
      newArray.push({
        listItem: this.state.listItem,
        deadline: this.state.deadline
      })
      // newArray.push(this.state.deadline)
      this.setState({ listItem: '', deadline: '', todoList: newArray })
      return response.data
    } catch (error) {
      console.log(error)
    }
  }
  handleDeadline = (e) => {
    this.setState({ deadline: e.target.value })
  }
  render() {
    console.log(this.state.listItem)
    return (
      <div className="todomain">
        <h1 className="todoTitle">Project Planning</h1>
        <form onSubmit={this.handleSubmit} className="todoform">
          <input
            type="text"
            name="listItem"
            placeholder="add todo here"
            value={this.state.listItem}
            onChange={this.handleChange}
          />
          <input
            type="text"
            name="deadline"
            placeholder="deadline"
            value={this.state.deadline}
            onChange={this.handleDeadline}
          />
          {/* <button >Add item!</button> */}
          <input
            type="submit"
            value="submit"
            // onClick={(e) => this.additem(e)}
          />
        </form>
        <button className="clear" onClick={(e) => this.clearlist(e)}>
          Clear list!
        </button>
        {this.state.todoList.map((item, index) => (
          <ListItem
            doThis={item.listItem}
            deadline={item.deadline}
            key={'todo' + index}
          />
        ))}
        <Footer name={name} email={email} />
      </div>
    )
  }
}
